import { combineReducers } from "redux";

import user from "./user";
import post from "./post";

const initialState = {
  user: {},
  post: {},
};

export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutAction = (data) => {
  return {
    type: "LOG_OUT",
    data,
  };
};

const rootReducer = combineReducers({
  index: (state = {}, action) => {
    switch (action.type) {
      case value:
        break;

      default:
        break;
    }
  },
  user,
  post,
});

export default rootReducer;
