import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZoome] = useState(false);
  const onZoom = useCallback(() => {
    setShowImagesZoome(true);
  }, [showImagesZoom]);

  if (images.length === 1) {
    return (
      <>
        <div>
          <img
            role="presentation"
            src={images[0].src}
            alt={images[0].src}
            onClick={onZoom}
          />
        </div>
      </>
    );
  }

  if (images.length == 2) {
    return (
      <>
        <div>
          <img
            role="presentation"
            width="50%"
            src={images[0].src}
            alt={images[0].src}
            onClick={onZoom}
          />
          <img
            role="presentation"
            width="50%"
            src={images[1].src}
            alt={images[1].src}
            onClick={onZoom}
          />
        </div>
      </>
    );
  }
  return (
    <>
      <div>
        <img
          role="presentation"
          width="50%"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
        <div
          role="presentation"
          style={{
            display: "inline-block",
            width: "50%",
            textAlign: "center",
            verticalAlign: "middle",
          }}
          onClick={onZoom}
        >
          <PluseOutlined />
          <br></br>
        </div>
      </div>
    </>
  );
};

PostImages.propTypes = {};

export default PostImages;
