import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";
import { PlusOutlined } from "@ant-design/icons";

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZoome] = useState(false);
  const onZoom = useCallback(() => {
    setShowImagesZoome(true);
  }, [showImagesZoom]);

  const onClose = useCallback(() => {
    setShowImagesZoome
  }, [showImagesZoom])

  if (images.length === 1) {
    return (
      <>
        <div>
          <img
            role="presentation"
            src={images[0].src}
            alt={images[0].src}
            style={{ maxHeight: "200" }}
            onClick={onZoom}
          />
        </div>
        {showImagesZoom && <ImagesZoom image={images} onClose={onClose} />}
      </>
    );
  }

  if (images.length == 2) {
    return (
      <>
        <div>
          <img
            role="presentation"
            width="50%"
            src={images[0].src}
            alt={images[0].src}
            onClick={onZoom}
          />
          <img
            role="presentation"
            width="50%"
            src={images[1].src}
            alt={images[1].src}
            onClick={onZoom}
          />
        </div>
        {showImagesZoom && <ImagesZoom image={images} onClose={onClose} />}
      </>
    );
  }
  return (
    <>
      <div>
        <img
          role="presentation"
          width="50%"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        />
        <div
          role="presentation"
          style={{
            display: "inline-block",
            width: "50%",
            textAlign: "center",
            verticalAlign: "middle",
          }}
          onClick={onZoom}
        >
          <PlusOutlined />
          <br />
          {images.length - 1}
          개의 사진 더보기
        </div>
      </div>
      {showImagesZoom && <ImagesZoom image={images} onClose={onClose} />}
    </>
  );
};

PostImages.propTypes = {};

export default PostImages;
