export const initialState = {
  isLoggingIn: false, // 로그인 시도중
  isLoggedIn: false,
  isLoggingOut: false, // 로그아웃 시도중
  me: null,
  signUpData: {},
  loginData: {},
};

export const loginReQuestAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutReQuestAction = (data) => {
  return {
    type: "LOG_OUT",
    data,
  };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    // 로그인
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggingIn: true,
        me: action.data,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: { ...action.data, nickname: "hello" },
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
      };

    // 로그아웃
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggingOut: true,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
      };

    default:
      return state;
  }
};

export default reducer;
